     7	    <div className='timer container d-flex '>
     8	      Only {days} Days and {hours} Hours Until
     9	    </div>
    10	  );
    11	};
    12	
    13	class DateCardComponent extends Component {
    14	  constructor(props) {
    15	    super(props);
    16	    this.state = {
    17	      id: this.props.id,
    18	      date: this.props.date,
    19	      description: this.props.description,
    20	      name: this.props.name,
    21	      isOpen: false,
    22	      renderer: renderer,
    23	      currentYear: new Date().getFullYear(),
    24	    };
    25	  }
    26	
    27	  //each card gets its own unique light color
    28	  getColor() {
    29	    let color = '#' + Math.floor(Math.random() * 16777215).toString(16);
    30	    return this.lightenColor(color, 35);
    31	  }
    32	
    33	  //build a toggle function for the dropdown
    34	  toggle() {
    35	    this.setState({
    36	      isOpen: !this.state.isOpen,
    37	    });
    38	  }
    39	
    40	  lightenColor(color, percent) {
    41	    const num = parseInt(color.replace('#', ''), 16),
    42	      amt = Math.round(2.55 * percent),
    43	      R = (num >> 16) + amt,
    44	      B = ((num >> 8) & 0x00ff) + amt,
    45	      G = (num & 0x0000ff) + amt;
    46	    return (
    47	      '#' +
    48	      (
    49	        0x1000000 +
    50	        (R < 255 ? (R < 1 ? 0 : R) : 255) * 0x10000 +
    51	        (B < 255 ? (B < 1 ? 0 : B) : 255) * 0x100 +
    52	        (G < 255 ? (G < 1 ? 0 : G) : 255)
    53	      )
    54	        .toString(16)
    55	        .slice(1)
    56	    );
    57	  }
    58	
    59	  render() {
    60	    return (
    61	      <div
    62	        className='buttonBox'
    63	        style={{ backgroundColor: this.getColor() }}
    64	        alt={this.state.name}
    65	      >
    66	        <div className='card-body'>
    67	          <div className='card-text'>
    68	            <Countdown
    69	              date={new Date(this.state.date)}
